package com.naman;

public class Student 
{
	int rollNo;
	String name;
	public int getRollNo() {
		return rollNo;
	}
	public void setRollNo(int rollNo) {
		this.rollNo = rollNo;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public Student(int rollNo, String name) 
	{
		super();
	
		    // TODO Auto-generated constructor stub
			this.rollNo = rollNo;
			this.name = name;
	}
	
	
	

}
